mapper.delete:
update item set yn=1 where is_delete=0

   and id = #{id,jdbcType=BIGINT}


   and title = #{title,jdbcType=VARCHAR}


   and sell_point = #{sellPoint,jdbcType=VARCHAR}


   and price = #{price,jdbcType=BIGINT}


   and num = #{num,jdbcType=INTEGER}


   and barcode = #{barcode,jdbcType=VARCHAR}


   and image = #{image,jdbcType=VARCHAR}


   and cid = #{cid,jdbcType=BIGINT}


   and status = #{status,jdbcType=BINARY}


   and created = #{created,jdbcType=TIMESTAMP}


   and updated = #{updated,jdbcType=TIMESTAMP}

